<template>
  <div class="lee-slide-check"
       :style="{fontSize:fontSize+'px',width:width+'px',lineHeight:height+'px',height:height+'px',width:width+'px'}">
    <div class="slide-bg" :style="{transition:'all '+ms+' ease-out',width:moveX+'px',background:bgColor}"></div>
    <div class="slide-text">
      <template v-if="!isOk">拖动滑块验证</template>
      <template v-else><i class="b">验证通过</i></template>
    </div>
    <div class="handle" :class="{ok:isOk}" ref="handle" :style="{transition:'all '+ms+' ease-out',left:moveX+'px'}"
         @mousedown.prevent="mouseDown" @mousemove.prevent="mouseMove" @mouseup.prevent="mouseUp"
         @touchstart.stop="touchStart" @touchmove.stop="touchMove" @touchend.stop="touchEnd"></div>
  </div>
</template>
<script>
export default {
    name: 'LeeSlideCheck',
    inheritAttrs: false,
    data() {
        return {
            moveX: 0,
            isOk: false,
            isDown: false,
            oldX: 0,
            ms: '0s'
        }
    },
    mounted() {
        document.addEventListener('mousedown', (e) => {
            this.oldX = e.pageX
            this.isDown = true
        })
        document.addEventListener('mousemove', (e) => {
            e.stopPropagation()
            if (this.isDown) {
                let target = this.$refs.handle
                this.moveX = e.pageX - this.oldX
                if (this.moveX <= 0) {
                    this.moveX = 0
                }
                if (this.moveX >= this.width - target.getBoundingClientRect().width) {
                    this.moveX = this.width - target.getBoundingClientRect().width
                    this.isOk = true
                }

            }


        })
        document.addEventListener('mouseup', () => {
            this.isDown = false
        })
    },
    methods: {
        mouseDown(e) {
            this.oldX = e.pageX
            this.isDown = true
        },
        mouseMove(e) {
            if (this.isDown) {
                let target = e.currentTarget
                this.moveX = e.pageX - this.oldX
                if (this.moveX <= 0) {
                    this.moveX = 0
                }
                if (this.moveX >= this.width - target.getBoundingClientRect().width) {
                    this.moveX = this.width - target.getBoundingClientRect().width
                    this.isOk = true
                }

            }
        },
        mouseUp() {
            this.isDown = false
        },
        touchStart(e) {
            this.oldX = e.targetTouches[0].pageX
            this.isDown = true
        },
        touchMove(e) {
            if (this.isDown) {
                let target = e.currentTarget
                this.moveX = e.targetTouches[0].pageX - this.oldX
                if (this.moveX <= 0) {
                    this.moveX = 0
                }
                if (this.moveX >= this.width - target.getBoundingClientRect().width) {
                    this.moveX = this.width - target.getBoundingClientRect().width
                    this.isOk = true
                }

            }
        },
        touchEnd() {
            this.isDown = false
        },
    },
    props: {
        fontSize: {
            type: Number,
            default: 12
        },
        width: {
            type: Number,
            default: 300
        },
        height: {
            type: Number,
            default: 34
        },
        bgColor: {
            type: String,
            default: '#7ac23c'
        }
    },
    watch: {
        isDown: {
            immediate: false,
            handler(value) {
                if (!value && !this.isOk) {
                    this.moveX = 0
                    this.ms = '.2s'
                    this.$emit('end', 'no')
                } else {
                    this.ms = '0s'
                }
            }
        },
        isOk: {
            immediate: true,
            handler(value) {
                if (value) {
                    this.$emit('end', 'ok')
                }
            }
        }
    },
};
</script>
<style>
.lee-slide-check {
  position: relative;
  background-color: #e8e8e8;
  text-align: center;
}

.lee-slide-check .slide-bg {
  height: 100%;
}

.lee-slide-check .slide-text {
  -webkit-user-select: none;
  width: 100%;
  height: 100%;
  position: absolute;
  text-align: center;
  top: 0px;
  left: 0;
}

.lee-slide-check .slide-text .b {
  color: #fff;
  font-style: normal
}


.lee-slide-check .handle {
  position: absolute;
  top: 0px;
  width: 20%;
  height: 100%;
  border: 1px solid #ccc;
  cursor: move;
  background: #fff url(data:image/png;base64,) no-repeat center;
}

.lee-slide-check .handle.ok {
  pointer-events: none;
  background: #fff url(data:image/png;base64,) no-repeat center;
}

</style>
